rule "Meraki - frame_control"
when
  has_field(
    field : "meraki_fc_type"
  )
  AND
  has_field(
    field : "meraki_fc_subtype"
  )
then
  route_to_stream(
    name : "Field Type Conversion",
    remove_from_default : true
  );
  let output_1 = get_field(
    field : "meraki_fc_type"
  );
  let output_2 = get_field(
    field : "meraki_fc_subtype"
  );
  let output_3 = to_string(
    value : output_1
  );
  let output_4 = to_string(
    value : output_2
  );
  let output_5 = concat(
    first : output_3,
    second : "_"
  );
  let output_6 = concat(
    first : output_5,
    second : output_4
  );
  set_field(
    field : "meraki_fc_combined",
    value : output_6
  );
  let gl2_fragment_extractor_1743194726951 = lookup_value("Meraki - FC Combined", to_string($message."meraki_fc_combined"));
set_field("meraki_frame_control", gl2_fragment_extractor_1743194726951);
  let output_7 = gl2_fragment_extractor_1743194726951;
  set_field(
    field : "meraki_frame_control",
    value : output_7
  );
  remove_single_field(
    field : "meraki_fc_combined"
  );
  remove_from_stream(
    name : "Field Type Conversion"
  );
  route_to_stream(
    name : "Cisco Meraki",
    remove_from_default : true
  );
end

