rule "Meraki - Flows"
when
  grok(
  value: to_string($message."message"),
  pattern: "<%{BASE10NUM:syslog_priority}>%{BASE10NUM:syslog_ver} %{BASE10NUM:epoch} %{DATA:host} flows"
).matches == true
then
  let gl2_fragment_grok_results = grok(
  pattern: "<%{BASE10NUM:syslog_priority:INT}>%{BASE10NUM:syslog_ver:INT} %{BASE10NUM:epoch} %{DATA:host} %{WORD:category} %{WORD:action}",
  value: to_string($message."message")
);
set_fields(
  fields: gl2_fragment_grok_results,
  prefix: "meraki_"
);
  let gl2_fragment_grok_results = grok(
  pattern: "src=%{IP:src_ip}",
  value: to_string($message."message")
);
set_fields(
  fields: gl2_fragment_grok_results,
  prefix: "meraki_"
);
  let gl2_fragment_grok_results = grok(
  pattern: "dst=%{IP:dst_ip}",
  value: to_string($message."message")
);
