  fields: gl2_fragment_grok_results,
  prefix: "meraki_"
);
  let gl2_fragment_grok_results = grok(
  pattern: "mac=%{COMMONMAC:mac_address}",
  value: to_string($message."message")
);
set_fields(
  fields: gl2_fragment_grok_results,
  prefix: "meraki_"
);
  let gl2_fragment_grok_results = grok(
  pattern: "protocol=%{WORD:protocol}",
  value: to_string($message."message")
);
set_fields(
  fields: gl2_fragment_grok_results,
  prefix: "meraki_"
);
  let gl2_fragment_grok_results = grok(
  pattern: "protocol=%{INT:protocol_number:INT}",
  value: to_string($message."message")
);
set_fields(
  fields: gl2_fragment_grok_results,
  prefix: "meraki_"
);
  let gl2_fragment_grok_results = grok(
  pattern: "sport=%{INT:src_port:INT}",
  value: to_string($message."message")
);
set_fields(
  fields: gl2_fragment_grok_results,
  prefix: "meraki_"
);
  let gl2_fragment_grok_results = grok(
  pattern: "dport=%{INT:dst_port:INT}",
  value: to_string($message."message")
);
set_fields(
