rule "Meraki - UNIX Epoch Conversion with Timezone"
when
  has_field("meraki_epoch")
then
  // Step 1: Get raw epoch value as string
  let raw_epoch_str = to_string(get_field("meraki_epoch"));
  //set_field("debug_raw_epoch_str", raw_epoch_str);

  // Step 2: Convert to floating-point number to handle decimals
  let raw_epoch = to_double(raw_epoch_str);
  //set_field("debug_raw_epoch", raw_epoch);

  // Step 3: Drop the decimal part and convert to whole seconds (round down)
  let epoch_seconds = to_long(raw_epoch);  // Dropping the fractional part
  //set_field("debug_epoch_seconds", epoch_seconds);

  // Step 4: Convert the epoch seconds to milliseconds
  let epoch_ms = epoch_seconds * 1000;  // Convert to milliseconds
  //set_field("debug_epoch_ms", epoch_ms);

  // Step 5: Use parse_unix_milliseconds to convert the epoch timestamp (milliseconds) to a date
  let converted_date = parse_unix_milliseconds(epoch_ms, "America/Los_Angeles");
 // set_field("debug_converted_date_tz", converted_date);

  // Step 6: Store the correctly parsed and timezone-adjusted timestamp
  set_field("meraki_timestamp", converted_date);
end

