rule "Meraki - Extract Syslog PRI"
when
    has_field("message")
then
  set_fields(
    grok(
      pattern: "<%{NONNEGINT:meraki_syslog_priority:int}>",
      value: to_string($message.message),
      only_named_captures: true
    )
  ); 

  // Get numeric parts
  let priority = expand_syslog_priority(to_long($message.meraki_syslog_priority));
  set_field("meraki_facility", priority.facility);
  set_field("meraki_severity", priority.level);

  // Get string descriptions
